<script>
// Appelle des fonctions de gestions des cookies contenues dans BG-fonctions.js

function block_afficher_masquer__masquer_afficher(bouton_esc) {
//	var bouton_esc = document.getElementById(id_bouton_esc);
	/*
	// déterminer bloc_esc par le DOM, comme ci-dessous, serait trop risqué : l'architecture peut changer
	var bloc_esc = bouton_esc.parentElement.parentElement.parentElement.nextElementSibling;
	*/
	// recherche du bloc d'entrainement qui renferme le bouton escape actionné
	var bloc_entrainement = bouton_esc.closest('.ec__groupe-entrainement');
	var id_bloc_entrainement = bloc_entrainement.id; // sera utile pour le cookie stockant le statut du bloc_esc
	// recherche du bloc qui encadre le morceau. Il est utile de le connaître pour faire varier son padding en fontion du statut (affiché/masqué) du module 
	var bloc_encadrant_morceau = bouton_esc.closest('.ec__un-morceau');
	// recherche du bloc escamotable
	var bloc_esc = bloc_entrainement.getElementsByClassName('bloc-esc')[0];
									  

    if(bouton_esc.checked)
    {
        bloc_esc.style.display="block";
		//restituer les padding du bloc entrainement
		bloc_encadrant_morceau.style.paddingTop = bloc_encadrant_morceau_padding_top;
		bloc_encadrant_morceau.style.paddingBottom = bloc_encadrant_morceau_padding_bottom;
		createCookie('vt-'+id_bloc_entrainement,"Affiché",365);
    }
    else
    {
        bloc_esc.style.display="none";
        //réduire les paddings du bloc entrainement pour qu'il occupe moins de place
		bloc_encadrant_morceau.style.paddingTop = "5px";
		bloc_encadrant_morceau.style.paddingBottom = "5px";
		createCookie('vt-'+id_bloc_entrainement,"Masqué",365);
    }
    return true;
}

</script>


