<script>
/*
Mise en conformité des modules avec leurs cookies qui indiquent si le module est affiché intégralement ou non
*/

// variables globales qui permettent de régler le padding du module (bloc_entrainement) en fonction de son état (affiché/masqué)
// utilisés dans la fonction 'block_afficher_masquer__masquer_afficher'
var bloc_encadrant_morceau_padding_top = "0";
var bloc_encadrant_morceau_padding_top = "0";

function affichage_masquage_modules()
{
	// repérer tous les boutons de masquage
	var boutons_esc = document.getElementsByClassName('bouton-bloc-esc');
	if (boutons_esc != null) {
		for (var i= 0; i < boutons_esc.length; i++) {
			var bloc_entrainement = boutons_esc[i].closest('.ec__groupe-entrainement');
			var bloc_encadrant_morceau = boutons_esc[i].closest('.ec__un-morceau');
			if (i == 0) { // on sauvegaerde les valeurs de padding du bloc encadrand le morceau, car on les fait varier pour des raisons esthétiques en fonction de l'état affiché/masqué du bloc masquable
				var style_bloc_encadrant_morceau = window.getComputedStyle(bloc_encadrant_morceau, null);
				bloc_encadrant_morceau_padding_top = style_bloc_encadrant_morceau.getPropertyValue("padding-top");
				bloc_encadrant_morceau_padding_bottom = style_bloc_encadrant_morceau.getPropertyValue("padding-bottom");
			}
			var bloc_esc = bloc_entrainement.getElementsByClassName('bloc-esc')[0];
			var id_bloc_entrainement = bloc_entrainement.id;
			var etat_initial = "Affiché";
			var cook = readCookie('vt-'+id_bloc_entrainement);					  
			if (cook != null) {
				etat_initial = cook;
			}
			if (etat_initial=="Affiché")
			{
				bloc_esc.style.display="block";
				boutons_esc[i].checked = true;
			}
			else
			{
				bloc_esc.style.display="none";
				boutons_esc[i].checked = false;
				bloc_encadrant_morceau.style.paddingTop = "5px";
				bloc_encadrant_morceau.style.paddingBottom = "5px";
			}			
		}
	}
}
document.onload = affichage_masquage_modules();
</script>
